import React,{useState,useEffect} from "react";
import { io } from "socket.io-client";
const socket = io("http://localhost:5000");

export default function Chat({username,room}){
  const [msg,setMsg]=useState("");
  const [messages,setMessages]=useState([]);
  const [users,setUsers]=useState([]);

  useEffect(()=>{
    socket.emit("join_room",{username,room});
    socket.on("receive_message",data=>{
      setMessages(prev=>[...prev,data]);
    });
    socket.on("room_users",(list)=>{
      setUsers(list);
    });
  },[]);

  const send=()=>{
    if(!msg) return;
    socket.emit("send_message",{username,room,message:msg,time:new Date().toLocaleTimeString()});
    setMsg("");
  };

  return(
    <div style={{padding:20}}>
      <h3>Room: {room}</h3>
      <div>Users: {users.map(u=>u.username).join(", ")}</div>
      <div style={{height:300,overflowY:"auto",border:"1px solid #aaa",margin:"10px 0",padding:10}}>
        {messages.map((m,i)=>(
          <div key={i}><b>{m.username}</b>: {m.message} <i>({m.time})</i></div>
        ))}
      </div>
      <input value={msg} onChange={e=>setMsg(e.target.value)} placeholder="Type message"/>
      <button onClick={send}>Send</button>
    </div>
  )
}
